import React, { useEffect, useState } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './Map.css'; // 新しく追加したCSSファイルをインポート

const containerStyle = {
    width: '100%',
    height: '100vh',
};

const center = {
    lat: 35.681236,
    lng: 139.767125
};

type Position = {
    lat: number,
    lng: number
}

const Map: React.FC = () => {
    const [locations, setLocations] = useState<Position[]>([
        { lat: 35.681236, lng: 139.767125 },
    ]);

    const [isLoggedIn, setIsLoggedIn] = useState<boolean>(false);

    // ハンバーガーアイコンの動作
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);

    const toggleSidebar = () => {
        setIsSidebarOpen(!isSidebarOpen);
    };

    // APIから位置データを取得
    const fetchData = async () => {
        try {
            const result = await axios.get('http://localhost:8000/api/locations/');
            setLocations(result.data.map((location: any) => ({lat: location.lat, lng: location.lng})));
        } catch (error) {
            console.error(error);
        }
    };

    useEffect(() => {
        // 初期データを設定した後にAPIからデータを取得
        fetchData();

        // ログイン状態をチェック
        const token = localStorage.getItem('token');
        setIsLoggedIn(!!token);
    }, []);

    const handleLogout = () => {
        // ログアウト処理
        localStorage.removeItem('token');
        setIsLoggedIn(false);
    };

    return (
        <div>
            {/* ハンバーガーメニューアイコン */}
            <div className="hamburger" onClick={toggleSidebar}>
                <div className="bar"></div>
                <div className="bar"></div>
                <div className="bar"></div>
            </div>

            {/* サイドバー */}
            {isSidebarOpen && (
                <div className="sidebar">
                    {/* 閉じるボタン */}
                    <button className="hamburger" onClick={toggleSidebar}>⇦</button>
                    {/* ログインしている場合はログアウトボタンを表示 */}
                    {isLoggedIn ? (
                        <button onClick={handleLogout}>ログアウト</button>
                    ) : (
                        <div>
                            <Link to="/login"><button>ログイン</button></Link>
                            <Link to="/signup"><button>サインアップ</button></Link>
                        </div>
                    )}
                </div>
            )}
            {/* マップ表示 */}
            <LoadScript googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY || ''}>
                <GoogleMap
                    mapContainerStyle={containerStyle}
                    center={center}
                    zoom={10}
                >
                    {/* DBカラ取得した位置データに基づいて複数のマーカーを描画 */}
                    {locations.map((location, i) => (
                        <Marker key={i} position={location} />
                    ))}
                </GoogleMap>
            </LoadScript>
        </div>
    );
}

export default Map;
