import React, { useEffect, useState } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import axios from 'axios';
import { Link } from 'react-router-dom';

const containerStyle = {
    width: '100%',
    height: '100vh',
    display: 'flex', // ヘッダーを縦に配置するために追加
    flexDirection: 'column', // ヘッダーを縦に配置するために追加
};

const center = {
    lat: 35.681236,
    lng: 139.767125
};

type Position = {
    lat: number,
    lng: number
}

const Map: React.FC = () => {
    const [locations, setLocations] = useState<Position[]>([
        { lat: 35.681236, lng: 139.767125 },
    ]);

    const [isLoggedIn, setIsLoggedIn] = useState<boolean>(false);

    // APIから位置データを取得
    const fetchData = async () => {
        try {
            const result = await axios.get('http://localhost:8000/api/locations/');
            setLocations(result.data.map((location: any) => ({lat: location.lat, lng: location.lng})));
        } catch (error) {
            console.error(error);
        }
    };

    useEffect(() => {
        // 初期データを設定した後にAPIからデータを取得
        fetchData();

        // ログイン状態をチェック
        const token = localStorage.getItem('token');
        setIsLoggedIn(!!token);
    }, []);

    const handleLogout = () => {
        // ログアウト処理
        localStorage.removeItem('token');
        setIsLoggedIn(false);
    };

    return (
        <div>
            {/* 左側の縦長のボタンエリア */}
            <div style={{ display: 'flex', flexDirection: 'column' }}>
                {/* ログインしている場合はログアウトボタンを表示 */}
                {isLoggedIn ? (
                    <button onClick={handleLogout}>ログアウト</button>
                ) : (
                    // ログインしていない場合はログインとサインアップボタンを表示
                    <div>
                        <Link to="/login">
                            <button>ログイン</button>
                        </Link>
                        <Link to="/signup">
                            <button>サインアップ</button>
                        </Link>
                    </div>
                )}
            </div>
            {/* マップ表示 */}
            <LoadScript googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY || ''}>
                <GoogleMap
                    mapContainerStyle={containerStyle}
                    center={center}
                    zoom={10}
                >
                    {/* DBカラ取得した位置データに基づいて複数のマーカーを描画 */}
                    {locations.map((location, i) => (
                        <Marker key={i} position={location} />
                    ))}
                </GoogleMap>
            </LoadScript>
        </div>
    );
}

export default Map;
