import React, { useState } from 'react';
import axios, { AxiosError } from 'axios';

const Signup: React.FC = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState(''); // エラーメッセージ用の状態

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();
    setErrorMessage(''); // エラーメッセージをクリア
    try {
      const response = await axios.post('http://localhost:8000/signup/', {
        email: email,
        password: password
      });
      if (response.status === 201) {
        // ここでログインページや /map へのリダイレクトを行う
      }
    } catch (error) {
        const axiosError = error as AxiosError; // エラーを AxiosError 型にキャスト
        if (axiosError.response && axiosError.response.status === 400) {
          setErrorMessage('既にこのユーザー情報は存在します');
        } else {
          setErrorMessage('登録中にエラーが発生しました');
        }
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Email:</label>
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
      </div>
      <div>
        <label>Password:</label>
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
      </div>
      {errorMessage && <div>{errorMessage}</div>} {/* エラーメッセージの表示 */}
      <button type="submit">Sign Up</button>
    </form>
  );
};

export default Signup;
