import React, { useState, useEffect } from 'react';
import { GoogleLogin } from 'react-google-login';
import axios from 'axios';

const Login: React.FC = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    useEffect(() => {
        const token = localStorage.getItem('token');
        setIsLoggedIn(!!token);
    }, []);

    const handleLoginSuccess = async (googleData: any) => {
        const result = await axios.post('http://localhost:8000/dj-rest-auth/google/', {
            access_token: googleData.tokebObj.id_token //Googleから受け取ったIDトークン
        });

        if (result.status === 200) {
            localStorage.setItem('token', result.data.key);
            setIsLoggedIn(true);
        } else {
            console.error("Google login error", result);
        }
    };

    const handleLoginFailure = (response: any) => {
        console.error(response);
    };

    if (isLoggedIn) {
        return <div>Successfuly logged in!</div>;
    }

    return (
        <div>
            <GoogleLogin
                clientId={process.env.REACT_APP_GOOGLE_CLIENT_ID || ''}
                buttonText="Login with Googgle"
                onSuccess={handleLoginSuccess}
                onFailure={handleLoginFailure}
                cookiePolicy={'single_host_origin'}
            />
        </div>
    );

}

export default Login;
