// Map.tsx
import React, { useEffect, useState } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useAuth } from './AuthContext'; // AuthContext から認証関連のフックをインポート
import './Map.css';

const containerStyle = {
  width: '100%',
  height: '100vh',
};

const center = {
  lat: 35.681236,
  lng: 139.767125
};

type Position = {
  lat: number,
  lng: number
};

const Map: React.FC = () => {
  const [locations, setLocations] = useState<Position[]>([{ lat: 35.681236, lng: 139.767125 }]);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false); // サイドバーの状態
  const { isAuthenticated, setIsAuthenticated, logout } = useAuth();
  const navigate = useNavigate();

  // ログアウト処理
  const handleLogout = () => {
    logout(); // AuthContext のログアウト関数を呼び出す
    localStorage.removeItem('token'); // ローカルストレージからトークンを削除
    setIsAuthenticated(false); // 認証状態を更新
    navigate('/login'); // ログインページへリダイレクト
  };

  // サイドバーの開閉を制御する関数
  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  // ユーザーがログインしているかどうかをチェック
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true); // トークンが存在すれば認証済みとする
    } else {
      navigate('/login'); // トークンが存在しなければログインページへリダイレクト
    }
  }, [navigate]);

  // APIから位置データを取得
  useEffect(() => {
    const fetchData = async () => {
      try {
        const result = await axios.get('http://localhost:8000/api/locations/');
        setLocations(result.data.map((location: any) => ({ lat: location.lat, lng: location.lng })));
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      {/* ハンバーガーメニューアイコン */}
      <div className="hamburger" onClick={() => {}}>
        <div className="bar"></div>
        <div className="bar"></div>
        <div className="bar"></div>
      </div>

      {/* サイドバー */}
      <div className="sidebar">
        {/* ログアウトボタン */}
        <button onClick={handleLogout}>ログアウト</button>
        {/* 他のUI要素 */}
      </div>

      {/* マップ表示 */}
      <LoadScript googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY || ''}>
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={center}
          zoom={10}
        >
          {locations.map((location, i) => (
            <Marker key={i} position={location} />
          ))}
        </GoogleMap>
      </LoadScript>
    </div>
  );
};

export default Map;
